import os,sys
import requests
import pyotherside
from basedir import *
import sqlite3
import hashlib
import base64
import logging

logging.basicConfig(format='%(levelname)s:%(message)s', level=logging.DEBUG)
__appname__ = "harbour-sailfishclub"
dbPath = os.path.join(XDG_DATA_HOME, __appname__, __appname__, "QML", "OfflineStorage", "Databases")

emptyImage = ""


def getEtag(url):
    r = requests.head(url)
    header = r.headers
    etag = header.get("ETag")
    print(etag)
    return etag

def image_provider(image_id, requested_size ):
    if not image_id.startswith("http"):
        return bytearray(emptyImage), (-1, -1), pyotherside.format_data
    name = getMd5(image_id)
    etag, bindata = getData(name)
    newetag = getEtag(image_id)
    if not etag and etag == newetag:
        image_data = base64.b64decode(bindata)
    else:
        bindata = downloadImg(image_id)
        if bindata:
            insertDatas(name, newetag, bindata)
        else:
            bindata = emptyImage
        image_data = base64.b64decode(bindata)
    return bytearray(image_data), (-1, -1), pyotherside.format_data


def getDbname():
    dbname = getMd5(__appname__)
    return dbPath+"/"+dbname+".sqlite"


def getData(name):
    conn = sqlite3.connect(getDbname())
    try:
        cur = conn.cursor()
        cur.execute("SELECT etag,bindata FROM imgs WHERE  name ='%s' " % (name,))
        result = cur.fetchone()
        if result:
            return result[1], result[2]
        else:
            return None, None
    except Exception as e:
        logging.error("query error")
        return None, None
    finally:
        conn.close()


def insertDatas(name, etag, bindata):
    conn = sqlite3.connect(getDbname())
    try:
        cur = conn.cursor()
        cur.execute('''CREATE TABLE IF NOT EXISTS imgs
                (name VARCHAR(32) PRIMARY KEY,etag text,bindata text) ''')
        cur.commit()
        cur.execute("INSERT OR REPLACE INTO imgs VALUES (''%s',%s','%s')" % (name, etag, bindata))
        cur.commit()
    except Exception as e:
        logging.error("Insert error")
    finally:
        conn.close()


def downloadImg(url):
    try:
        r = requests.get(url)
        if r.status_code == 200:
            return base64.b64encode(r.content)
        return None
    except Exception as e:
        logging.error(str(e))
        return None

def getMd5(name):
    h = hashlib.md5()
    h.update(name.encode(encoding='utf-8', errors='strict'))
    return h.hexdigest()

pyotherside.set_image_provider(image_provider)
